mapper.insert:
insert into tbl_clue(
id, fullname, appellation, owner, company, job, email, phone, website, mphone, state, source, createBy, createTime, description, contactSummary, nextContactTime, address
) values (
#{id}, #{fullname}, #{appellation}, #{owner}, #{company}, #{job}, #{email}, #{phone}, #{website}, #{mphone}, #{state}, #{source}, #{createBy}, #{createTime}, #{description}, #{contactSummary}, #{nextContactTime}, #{address}
)

mapper.select:
select
c.id, c.fullname, c.appellation, u.name as owner, c.company, c.job, c.email, c.phone, c.website, c.mphone, c.state, c.source, c.createBy, c.createTime, c.description, c.contactSummary, c.nextContactTime, c.address
from tbl_clue c join tbl_user u on u.id=c.owner where c.id=#{id}
